      this._fadeTopBottom = new DvtMask(new DvtLinearGradientFill(this._isHorizontalScrolling ? 180 : 90, ['#FFFFFF', '#FFFFFF', '#FFFFFF', '#FFFFFF'], [0, 1, 1, 0], [0, 0.1, 0.9, 1]), dims);

